import React, { useState, useEffect } from 'react';
import './App.css'; // Import a CSS file for styling
import { useHistory } from 'react-router-dom'; // Assuming you're using react-router for navigation

function App() {
  const [amount, setAmount] = useState('');
  const [riskTolerance, setRiskTolerance] = useState('medium');
  const [portfolio, setPortfolio] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [hasAccess, setHasAccess] = useState(null); // Track access status

  const history = useHistory(); // Use history for redirection

  // Your Heroku app URL here
  const API_URL = 'https://portfolioai-da34d7ab7951.herokuapp.com/';
  const ACCESS_CHECK_URL = 'https://portfoliogpt.xyz/api/check-access'; // Replace with your Next.js API URL

  // Check if the user has access on component mount
  useEffect(() => {
    const checkAccess = async () => {
      try {
        const response = await fetch(ACCESS_CHECK_URL, {
          credentials: 'include', // Include cookies (for session-based authentication)
        });

        const data = await response.json();

        if (response.ok && data.hasAccess) {
          setHasAccess(true);
        } else {
          setHasAccess(false);
          history.push('/payment-required'); // Redirect if they haven't paid
        }
      } catch (err) {
        console.error('Error checking access:', err);
        setError('Unable to check access. Please try again later.');
      }
    };

    checkAccess();
  }, [history]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/generate_portfolio`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount, risk_tolerance: riskTolerance }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Unknown error');
      }

      const data = await response.json();
      setPortfolio(typeof data.portfolio === 'object' ? JSON.stringify(data.portfolio, null, 2) : data.portfolio);
    } catch (error) {
      console.error('Error fetching portfolio:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  if (hasAccess === null) {
    return <div>Loading...</div>; // Loading state while checking access
  }

  if (hasAccess === false) {
    return <div>You do not have access to this service.</div>; // Message for users without access
  }

  return (
    <div className="app-container">
      <h1>Portfolio Generator</h1>
      <form className="portfolio-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="amount">Investment Amount:</label>
          <input
            id="amount"
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="Enter amount"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="riskTolerance">Risk Tolerance:</label>
          <select
            id="riskTolerance"
            value={riskTolerance}
            onChange={(e) => setRiskTolerance(e.target.value)}
            required
          >
            <option value="low">Low (Conservative)</option>
            <option value="medium">Medium (Balanced)</option>
            <option value="high">High (Aggressive)</option>
          </select>
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Generating...' : 'Generate Portfolio'}
        </button>
      </form>
      {error && <div className="error-message">{error}</div>}
      {portfolio && (
        <div className="portfolio-container">
          <h2>Generated Portfolio:</h2>
          <pre className="portfolio-text">{portfolio}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
