import React from 'react';
import { Line } from 'react-chartjs-2';

const PortfolioChart = ({ data }) => {
  const chartData = {
    labels: data.dates,
    datasets: data.assets.map(asset => ({
      label: asset.name,
      data: asset.values,
      fill: false,
      borderColor: asset.color
    }))
  };

  return <Line data={chartData} />;
};

export default PortfolioChart;
