import React, { useState } from 'react';
import './App.css'; // Import a CSS file for styling

function App() {
  const [amount, setAmount] = useState('');
  const [riskTolerance, setRiskTolerance] = useState('medium');
  const [investmentDuration, setInvestmentDuration] = useState('long-term'); // New field for duration
  const [investmentGoal, setInvestmentGoal] = useState('growth'); // New field for goal
  const [preferredAssetClasses, setPreferredAssetClasses] = useState([]); // New field for preferred assets
  const [portfolio, setPortfolio] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Use your Heroku app URL here
  const API_URL = 'https://localhost:3000';

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/generate_portfolio`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          amount,  // Existing field
          risk_tolerance: riskTolerance,  // Existing field
          investment_duration: investmentDuration,  // New field
          investment_goal: investmentGoal,  // New field
          preferred_asset_classes: preferredAssetClasses  // New field
        }),
      });

      if (!response.ok) {
        if (response.status === 429) {
          throw new Error('Too many requests. Please try again later.');
        } else {
          const errorData = await response.json();
          throw new Error(errorData.error || 'Unknown error');
        }
      }

      const data = await response.json();
      setPortfolio(typeof data.portfolio === 'object' ? JSON.stringify(data.portfolio, null, 2) : data.portfolio);
    } catch (error) {
      console.error('Error fetching portfolio:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h1>Portfolio Generator</h1>
      <form className="portfolio-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="amount">Investment Amount:</label>
          <input
            id="amount"
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="Enter amount"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="riskTolerance">Risk Tolerance:</label>
          <select
            id="riskTolerance"
            value={riskTolerance}
            onChange={(e) => setRiskTolerance(e.target.value)}
            required
          >
            <option value="low">Low (Conservative)</option>
            <option value="medium">Medium (Balanced)</option>
            <option value="high">High (Aggressive)</option>
          </select>
        </div>

        {/* New fields for investment duration, goal, and preferred assets */}
        <div className="form-group">
          <label htmlFor="investmentDuration">Investment Duration:</label>
          <select
            id="investmentDuration"
            value={investmentDuration}
            onChange={(e) => setInvestmentDuration(e.target.value)}
            required
          >
            <option value="short-term">Short-term</option>
            <option value="long-term">Long-term</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="investmentGoal">Investment Goal:</label>
          <select
            id="investmentGoal"
            value={investmentGoal}
            onChange={(e) => setInvestmentGoal(e.target.value)}
            required
          >
            <option value="growth">Growth</option>
            <option value="income">Income</option>
            <option value="capital-preservation">Capital Preservation</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="preferredAssetClasses">Preferred Asset Classes:</label>
          <input
            id="preferredAssetClasses"
            type="text"
            value={preferredAssetClasses}
            onChange={(e) => setPreferredAssetClasses(e.target.value.split(','))}  // Convert CSV input to array
            placeholder="Enter asset classes (comma-separated, e.g., stocks,bonds,real estate)"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Generating...' : 'Generate Portfolio'}
        </button>
      </form>
      {error && <div className="error-message">{error}</div>}
      {portfolio && (
        <div className="portfolio-container">
          <h2>Generated Portfolio:</h2>
          <pre className="portfolio-text">{portfolio}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
