import React, { useState } from 'react';
import PortfolioChart from './PortfolioChart';

function App() {
  const [amount, setAmount] = useState('');
  const [riskTolerance, setRiskTolerance] = useState('');
  const [portfolio, setPortfolio] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/generate_portfolio', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ amount, riskTolerance })
    });
    const data = await response.json();
    setPortfolio(data.portfolio);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Investment Amount"
        />
        <input
          type="text"
          value={riskTolerance}
          onChange={(e) => setRiskTolerance(e.target.value)}
          placeholder="Risk Tolerance"
        />
        <button type="submit">Generate Portfolio</button>
      </form>
      {portfolio && <div>{portfolio}</div>}
      {/* Include PortfolioChart component when data is available */}
      {portfolio && <PortfolioChart data={portfolio} />}
    </div>
  );
}

export default App;
